package com.example.myapplication;import android.content.Intent;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.widget.TextView;import butterknife.BindView;import butterknife.ButterKnife;import butterknife.OnClick;public class Main2Activity extends AppCompatActivity {  @BindView(R.id.activ2)  TextView Фамилия;  @Override  protected void onCreate(Bundle savedInstanceState) {    super.onCreate(savedInstanceState);    setContentView(R.layout.activity_main2);    ButterKnife.bind(this);    Bundle b = getIntent().getExtras();    String value="";    if(b != null)      value = b.getString("Name");    Фамилия.setText("Привет " +value);  }}